mapper.select:
select 'true' as QUERYID,

from jun_xian_day
where id = #{id,jdbcType=INTEGER}

mapper.update:
update jun_xian_day
set symbol = #{symbol,jdbcType=CHAR},
  type = #{type,jdbcType=INTEGER},
  price = #{price,jdbcType=REAL},
  day = #{day,jdbcType=CHAR}
where id = #{id,jdbcType=INTEGER}
